mapper.insert:
select last_insert_id() as id
insert into MERCHANT_ORDER_INFO (
) values ( #{channelOrderId, jdbcType=VARCHAR}, #{merchantId, jdbcType=INTEGER}, #{merchantVersion, jdbcType=VARCHAR}, #{merchantOrderId, jdbcType=VARCHAR}, #{merchantOrderDate, jdbcType=VARCHAR}, #{merchatProductName, jdbcType=VARCHAR}, #{merchatProductId, jdbcType=VARCHAR}, #{merchatProductDesc, jdbcType=VARCHAR}, #{orderAmount, jdbcType=DECIMAL}, #{amount, jdbcType=DECIMAL}, #{payCharge, jdbcType=DECIMAL}, #{payBase, jdbcType=DECIMAL}, #{payAmount, jdbcType=DECIMAL}, #{payDivide, jdbcType=DECIMAL}, #{status, jdbcType=INTEGER}, #{statusCode, jdbcType=VARCHAR}, #{createDate, jdbcType=TIMESTAMP}, #{dealDate, jdbcType=TIMESTAMP}, #{payOrderId, jdbcType=VARCHAR}, #{payStatus, jdbcType=INTEGER}, #{payStatusCode, jdbcType=VARCHAR}, #{notifyStatus, jdbcType=INTEGER}, #{nodifyUrl, jdbcType=VARCHAR}, #{returnUrl, jdbcType=VARCHAR}, #{parameter1, jdbcType=VARCHAR}, #{appChannelId, jdbcType=VARCHAR}, #{notifyTimes, jdbcType=INTEGER}, #{notifyDate, jdbcType=TIMESTAMP}, #{paymentId, jdbcType=INTEGER}, #{channelId, jdbcType=INTEGER}, #{sourceType, jdbcType=DECIMAL}, #{memo, jdbcType=VARCHAR}, #{guid, jdbcType=VARCHAR}, #{userName, jdbcType=VARCHAR}, #{sourceUid, jdbcType=VARCHAR}, #{sourceUserName, jdbcType=VARCHAR}, #{clientostype, jdbcType=VARCHAR}, #{clientosversion, jdbcType=VARCHAR}, #{ip, jdbcType=VARCHAR}, #{imei, jdbcType=VARCHAR}, #{mac, jdbcType=VARCHAR}, #{appId, jdbcType=VARCHAR}, #{businessType, jdbcType=INTEGER}, #{statusMessage, jdbcType=VARCHAR} )

mapper.select:
select * from MERCHANT_ORDER_INFO
where  MERCHANT_ORDERID = #{merchantOrderId}
and APPID=#{appId}

 select * from  MERCHANT_ORDER_INFO where 1=1
 
    and ID=#{id} 
 
 
    and MERCHANT_ORDERID=#{merchantOrderId} 
 
 
    and PAY_ORDERID=#{payOrderId} 
 
 
    and CHANNEL_ID=#{channelId} 
 
 
    and PAY_STATUS=#{payStatus} 
 
 
    and PAYMENT_ID=#{paymentId} 
 
 
    and APPID=#{appId} 
 
 
    and CREATE_DATE BETWEEN #{startDate, jdbcType=TIMESTAMP} 
 
 
    and #{endDate, jdbcType=TIMESTAMP}
 
limit #{startRow},#{pageSize}

select * from  MERCHANT_ORDER_INFO where 1=1

   and ID=#{id} 


   and MERCHANT_ORDERID=#{merchantOrderId} 


   and PAY_ORDERID=#{payOrderId} 


   and CHANNEL_ID=#{channelId} 


   and PAY_STATUS=#{payStatus} 


   and PAYMENT_ID=#{paymentId} 


   and APPID=#{appId} 


   and CREATE_DATE BETWEEN #{startDate, jdbcType=TIMESTAMP} 


   and #{endDate, jdbcType=TIMESTAMP}

select count(*) from  MERCHANT_ORDER_INFO where 1=1

   and MERCHANT_ORDERID=#{merchantOrderId} 


   and PAY_ORDERID=#{payOrderId} 


   and CHANNEL_ID=#{channelId} 


   and PAY_STATUS=#{payStatus} 


   and PAYMENT_ID=#{paymentId} 


   and APPID=#{appId} 


   and CREATE_DATE BETWEEN #{startDate, jdbcType=TIMESTAMP} 


   and #{endDate, jdbcType=TIMESTAMP}

mapper.update:
update MERCHANT_ORDER_INFO set
NOTIFY_TIMES = #{notifyTimes}
where ID = #{id}

update MERCHANT_ORDER_INFO set
PAY_STATUS = #{payStatus}
where ID = #{id}

update MERCHANT_ORDER_INFO set
SOURCE_TYPE = #{sourceType}
where ID = #{id}

update MERCHANT_ORDER_INFO set
CHANNEL_ID = #{channelId}
where ID = #{id}

        update MERCHANT_ORDER_INFO set
        NOTIFY_STATUS = #{notifyStatus},
        NOTIFY_TIMES = #{notifyTimes},
	  	NOTIFY_DATE = #{notifyDate}
        where ID = #{id}

update MERCHANT_ORDER_INFO set
PAYMENT_ID = #{paymentId},
CHANNEL_ID = #{channelId},
SOURCE_TYPE = #{sourceType}
where ID = #{id}

       
        update MERCHANT_ORDER_INFO set
        ID = #{id},
		CREATE_DATE = #{createDate}
        where MERCHANT_ORDERID = #{merchantOrderId} and APPID=#{appId}

mapper.select:
select ID,MERCHANT_ORDERID merchantOrderId,MERCHANT_PRODUCTNAME merchantProductName,AMOUNT amount,PAYMENT_ID  paymentId,CHANNEL_ID channelId,SOURCE_UID sourceUid,SOURCE_USERNAME sourceUserName,APPID appId,PAY_STATUS payStatus,NOTIFY_STATUS notifyStatus from MERCHANT_ORDER_INFO
where 1=1 
and CREATE_DATE BETWEEN #{startTime, jdbcType=TIMESTAMP}  AND #{endTime, jdbcType=TIMESTAMP} and APPID=#{appId}

select count(MERCHANT_ORDERID) payCount  from MERCHANT_ORDER_INFO
where 1=1
 
  and APPID=#{appId} 
 
  
  and PAYMENT_ID=#{paymentId} 
 
  
  and CHANNEL_ID=#{channelId} 
 
and CREATE_DATE BETWEEN #{startTime, jdbcType=TIMESTAMP}  AND #{endTime, jdbcType=TIMESTAMP} and PAY_STATUS = 1 group by  date_format(CREATE_DATE,'%Y-%m-%d')

